% Attrs('QubeDocs Definition',!QubeDocs Definition,'Type') @= 'SecurityUser' % Attrs('QubeDocs Definition',!QubeDocs Definition,'Type') @= 'SecurityGroup',
Attrs('QubeDocs Definition',!QubeDocs Definition,'Type') | 's',
'AUTO');

